            siblings: "none",
            children: "none",
            medication1: "Aderal",
            dose1: "20mg per day",
            medication2: "Moteran",
            dose2: "80mg 2 per day"
        };
    }
    patientData2 = () => {
        return {
            chiefComplaint: "Adominal pain",
            pastMedicalHistory1: "Broken arm",
            pastSurgicalHistory1: "Arm removed",
            pastSocialHistory1: "Smokes socialy",
            pastMedicalHistory2: "none",
