            dose2: "none"
        };
    }
    patientData4 = () => {
        return {
            chiefComplaint: "Adominal pain",
            pastMedicalHistory1: "none",
            pastSurgicalHistory1: "none",
            pastSocialHistory1: "none",
            pastMedicalHistory2: "none",
            pastSurgicalHistory2: "none",
            pastSocialHistory2: "none",
