import React from 'react';
import { Form, Icon, Input, Button, Spin, Carousel } from 'antd';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import * as actions from '../store/actions/auth'

const antIcon = <Icon type="loading" style={{ fontSize: 24 }} spin />;

class NormalLoginForm extends React.Component {
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                // authenticate user here
                this.props.onAuth(values.userName, values.password)
            }
        });
        this.props.history.push('/play');
    }

    render() {

        let errorMessage = null;
        if (this.props.error) {
            errorMessage = (
                <p>{this.props.error.message}</p>
            );
        }

        const { getFieldDecorator } = this.props.form;
        return (
            <div>
                {errorMessage}
                {
                    this.props.loading ?

                        <Spin indicator={antIcon} />

                        :
                        <React.Fragment>
                            <Carousel autoplay style={{ height: '500px', background: '#364d79', overflow: 'hidden' }}>
                                <div><h3>AIHPI</h3><h4>All-Inclusive Health and Present Illness Report</h4></div>
                                <div><h3>Practice crafting reports.</h3></div>
                                <div><h3>Become the best medical practitioner you can be.</h3></div>
                                <div><h3>Learn the ways of the AIHPI</h3></div>
                            </Carousel>

                            <Form onSubmit={this.handleSubmit} className="login-form">
                                <Form.Item>
                                    {getFieldDecorator('userName', {
                                        rules: [{ required: true, message: 'Please input your username!' }],
                                    })(
                                        <Input prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />} placeholder="Username" />
                                    )}
                                </Form.Item>

                                <Form.Item>
                                    {getFieldDecorator('password', {
                                        rules: [{ required: true, message: 'Please input your Password!' }],
                                    })(
                                        <Input prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />} type="password" placeholder="Password" />
                                    )}
                                </Form.Item>

                                <Form.Item>
                                    <Button type="primary" htmlType="submit" style={{ marginRight: '10px' }}>
                                        Login
                            </Button>
                                    Or
                            <NavLink
                                        style={{ marginRight: '10px' }}
                                        to='/signup/'> signup
                            </NavLink>
                                </Form.Item>
                            </Form>
                        </React.Fragment>
                }
            </div>
        );
    }
}

const WrappedNormalLoginForm = Form.create({ name: 'normal_login' })(NormalLoginForm);

const mapStateToProps = (state) => {
    return {
        loading: state.loading,
        error: state.error
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (username, password) => dispatch(actions.authLogin(username, password))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(WrappedNormalLoginForm);
